INSERT INTO `tbl_faq` (`id`, `question`, `answer`, `status`) VALUES
(2, 'Šta cena uklju&#269;uje?', '<p>Svi sadržaji izlistani ispod tipa sobe, uklju&#269;eni su u cenu sobe. Da biste videli sadržaje, samo kliknite na naziv sobe. Možete proveriti da li su doru&#269;ak, ili bilo &scaron;ta drugo, kao &scaron;to su posebne takse, uklju&#269;eni u cenu, prelaze&#263;i kursorom preko teksta u koloni "Uslovi". Ova informacija bi&#263;e vam na raspolaganju i u potvrdi rezervacije, kao i na &lsquo;Moja rezervacija&rsquo;.</p>', 1),
(3, 'Mogu li izvršiti rezervaciju bez kreditne kartice?', '<p>Važe&#263;a kreditna kartica je neophodna kao garancija rezervacije hotela. Ipak, hotel možete rezervisati i uz pomo&#263; tu&#273;e kreditne kartice, pod uslovom da imate dozvolu vlasnika. Ako je ovo slu&#269;aj sa vama, molimo vas da prilikom rezervacije navedete ime vlasnika kartice, kao i to da imate ovla&scaron;&#263;enje da koristite karticu, u delu za napomene.</p>', 1),
(5, 'Kako da znam da je moja rezervacija potvr&#273;ena?', '<p>&#268;im zavr&scaron;ite proces rezervacije pojavi&#263;e se stranica sa potvrdom. Ova stranica sadrži sve detalje va&scaron;e rezervacije, uklju&#269;uju&#263;i i broj rezervacije i va&scaron; pin kod, koji vam omogu&#263;avaju da pristupite potvrdi na &lsquo;Moja rezervacija&rsquo;. Tako&#273;e &#263;emo vam poslati i imejl s potvrdom u kome se nalaze sve informacije vezane za rezervaciju.</p>', 1),
(6, 'Mogu li da otkažem ili izmenim rezervaciju preko Booking.com-a?', '<p>Da, vrlo jednostavno. Možete otkazati ili promeniti rezervaciju putem alatke &lsquo;Moja rezervacija&rsquo;. Molimo vas da proverite uslove otkazivanja samog hotela pre nego &scaron;to izmenite svoju rezervaciju. Sobe bez mogu&#263;nosti povra&#263;aja novca i druge specijalne ponude mogu imati razli&#269;ite uslove otkazivanja. Uslovi otkazivanja za odre&#273;enu sobu nalaze se pored vrste sobe, u delu naslovljenom "Uslovi".</p>', 1),
(7, 'toqeer', '<p>This is the answer</p>', 1),
(8, 'test', '<p>This is the test FAQ answers.</p>', 0);
